async function handleMessages(e){console.log("MESSAGE",e),"offscreen-doc"===e.target&&("copy-data-to-clipboard"===e.type?handleClipboardWrite(e.data):console.warn(`Unexpected message type received: '${e.type}'.`))}chrome.runtime.onMessage.addListener(handleMessages);let textEl=document.querySelector("#text");async function handleClipboardWrite(e){try{if("string"!=typeof e)throw new TypeError(`Value provided must be a 'string', got '${typeof e}'.`);textEl.value=e,textEl.select(),document.execCommand("copy")}finally{window.close()}}